    //This button adds an Album
    private void albumButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_albumButtonActionPerformed
        try {
            //Uses look_op so it can have access to the addAlbum() function and takes the appropriate fields in the correct order
            look_op.addAlbum(descriptionField.getText(),genreField.getText(),yearField.getText(),nameField.getText());
            //System.out.println("I am in ADDALBUM");
        } catch (RemoteException ex) {
            Logger.getLogger(UI.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_albumButtonActionPerformed
    //This button is used to delete an album
    private void deleteAlbumButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_deleteAlbumButtonActionPerformed
        try {
            //Calls deleteAlbum()
            look_op.deleteAlbum(delAlbumIDField.getText());
            //System.out.println("I am in DELETEALBUM");
        } catch (RemoteException ex) {
            Logger.getLogger(UI.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_deleteAlbumButtonActionPerformed
    //This button adds a song 
    private void songButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_songButtonActionPerformed
        try {
            
            look_op.addSong(titleField.getText(),artistField.getText(),durationField.getText(),idField.getText());
            //System.out.println("I am in ADDSONG");
        } catch (RemoteException ex) {
            Logger.getLogger(UI.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_songButtonActionPerformed
