    //Button that shows all the data that exist in the database 
    private void showButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_showButtonActionPerformed
        //Every time that wants to execute show it cleans up the textArea
        showTextArea.setText("");
        try {    
            //Calls showAll and prints the returning results
            String results=look_op.showAll();
            showTextArea.append(results);
            
        } catch (RemoteException ex) {
            Logger.getLogger(UI.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_showButtonActionPerformed
    //Button that edits the elements of an album
    private void updateAlbumButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_updateAlbumButtonActionPerformed
        try {
            //Calls updateAlbum and is given all the changes that need to be done
            look_op.updateAlbum(upDescriptionField.getText(), upNameField.getText(),upGenreField.getText(),upYearField.getText());
        } catch (RemoteException ex) {
            Logger.getLogger(UI.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_updateAlbumButtonActionPerformed
    //Button that edits the elements of a song
    private void updateSongButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_updateSongButtonActionPerformed
        try {
            look_op.updateSong(upTitleField.getText(),upArtistField.getText(),upDurationField.getText());
        } catch (RemoteException ex) {
            Logger.getLogger(UI.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_updateSongButtonActionPerformed

